import {useReducer} from 'react';
import ShopContext from './ShopContext';
import ShopReducer from './ShopReducer';
import {
    SET_LOADING,
    GET_CURRENT_SHOP,
    GET_CURRENT_SHOP_DAILY,
    GET_CURRENT_SHOP_FEATURED,
    SET_DAILY_INFO
} from '../types';
import axios from 'axios';


const ShopState = (props) => {
    

    const initialState = {
        loading: false,
        CurrentShop: {},
        CurrentDaily: [],
        CurrentFeatured: [],
        DailyInfo: {}
    }

    const [state, dispatch] = useReducer(ShopReducer, initialState);


    const SetLoading = () => {
        dispatch({type: SET_LOADING})
    }

    // Get Current Shop
    const GetCurrentShop = async () => {
        SetLoading();
        const response = await axios.get('https://fortnite-api.com/v2/shop/br');
        dispatch({
            type: GET_CURRENT_SHOP,
            payload: response.data.data
        })

        dispatch({
            type: GET_CURRENT_SHOP_DAILY,
            payload: response.data.data.daily
        })

        dispatch({
            type: GET_CURRENT_SHOP_FEATURED,
            payload: response.data.data.featured
        })
    }

    // Set Up Daily State - Breakdown Items

    const SetupDaily = () => {

        SetLoading();
        const CheckLength = () => {
            if(state.CurrentDaily.length > 0) {
                state.CurrentDaily.entries.map((item, i) => {
                return item
            })
        }
        dispatch({
            type: SET_DAILY_INFO,
            payload: CheckLength
        })

        return CheckLength;  
        }
    }
    
    

    return <ShopContext.Provider value={{
        loading: state.loading,
        CurrentShop: state.CurrentShop, 
        CurrentDaily: state.CurrentDaily, 
        CurrentFeatured: state.CurrentFeatured, 
        GetCurrentShop,
        SetUpDaily
    }}>

        {props.children}

    </ShopContext.Provider>;

}

export default ShopState;
