import React from 'react';
import ShopContext from '../../context/ShopContext/ShopContext';
import './FeaturedShopItem.css';
import {Link} from 'react-router-dom'
import {useContext} from 'react'

const FeaturedShopItem = ({FeaturedItems, DailyItems}) => {


    const sContext = useContext(ShopContext);

   const lol = 'x'
    return (
        <div className='shopitem shopitem--primary'>
            <Link to={`/shop/item/${lol}`} className='shopitemlink'>
                <p>{sContext.CurrentFeatured.map((item, i) => {
                    return item[i].finalPrice;
                })}</p>
            </Link>
        </div>
    )
}

export default FeaturedShopItem
