import React, {useContext, useEffect} from 'react';
import ShopContext from '../context/ShopContext/ShopContext';
import './Shop.css';
import ShopItem from './ShopItem/ShopItem';
import Spinner from '../../media/images/Spinner.svg'

const Shop = (props) => {
    
    const sContext = useContext(ShopContext);

    useEffect(() => {
        sContext.GetCurrentShop()
    }, [])
    console.log(sContext.loading)

    return (
        <div>
            {sContext.loading && sContext.CurrentDaily.entries.map((item, i) => {
                return <p>{item.finalPrice}</p>
            })}
        </div>
    )
}

export default Shop
