import React from 'react'
import {useContext} from 'react';
import ShopContext from '../../../context/ShopContext/ShopContext'

const FeaturedShopSection = () => {

    const sContext = useContext(ShopContext);

    return (
        <div className='featuredview featuredview--primary'>
            <p className='featuredview__head viewhead'>FEATURED</p>
            <div>
                <div className=''>
                    {sContext.CurrentFeatured && sContext.CurrentFeatured.FeaturedItemItemsImages.map((item, i) => {
                        
                        console.log(item)
                        if (item.length > 1) {
                            const first = item[0]
                            return <img src={first} style={{height: '50px', width: '50px'}}/>
                        } else {

                            return <img src={item} style={{height: '50px', width: '50px'}}/>
                        }

                        
                    })}
                </div>
                {sContext.CurrentFeatured && sContext.CurrentFeatured.FeaturedItemfinalPrices.map((item, i) => {
                    return <p>{item}</p>
                })}
            </div>




        </div>
    )
}

export default FeaturedShopSection
