import React from 'react';
import ShopContext from '../../context/ShopContext/ShopContext';
import './ShopItem.css';
// import {Link} from 'react-router-dom'

const ShopItem = ({match, DailyDetails}) => {


    const {items} = DailyDetails;

    const ItemDescription = items.map((item, i) => {
        return item.description;
    })

    const ItemImages = items.map((item, i) => {
        return item.images;
    })

    const ImageSRC = ItemImages.map((image, pos) => {

        if (image.featured) {
            return image.featured;
        } else {
            return image.icon;
        }
    })

    const ItemName = items.map((item, i) => {
        return item.name;
    })

    const ItemRarity = items.map((item, i) => {
        return item.rarity.displayValue;
    })

    console.log(ItemRarity)
   
    return (
        <>
        <div className='shopitem shopitem--primary'>
            <div className=''>
                <img className='shopitem__image' src={ImageSRC} />
            </div>
            <p>{ItemName}</p>
        </div>
        </>
    )
}

export default ShopItem
