import React, {useContext, useEffect} from 'react';
import ShopContext from '../context/ShopContext/ShopContext';
//import Spinner from '../../media/images/Spinner.svg'
import ShopItem from './ShopItem/ShopItem'
import './Shop.css';

const Shop = (props) => {
    
    const sContext = useContext(ShopContext);

    useEffect(() => {
        sContext.GetCurrentShop()
        // eslint-disable-next-line
    }, [])

    return (
        <div className='shop shop--primary'>
            <h1 className='shopview__head'>Today's Shop</h1>
            <div className="shopview shopview--primary">
                <div className='featuredview featuredview--primary'>
                    <p className='featuredview__head viewhead'>{sContext.CurrentShop && sContext.CurrentShop.featured.name}</p>
                    <div>
                        {sContext.CurrentShop && sContext.CurrentShop.featured.entries.map((item, i) => {
                            return <ShopItem ItemDetails={item} />
                        })}
                    </div>
                </div>
                <div className='dailyview dailyview--primary'>
                    <p className='dailyview__head viewhead'>{sContext.CurrentShop && sContext.CurrentShop.daily.name}</p>
                    {sContext.CurrentShop && sContext.CurrentShop.daily.entries.map((item, i) => {
                        return <ShopItem ItemDetails={item} />
                    })}
                </div>
            </div>
        </div>
    )
}

export default Shop
