import {
    SET_LOADING,
    GET_CURRENT_SHOP,
    GET_CURRENT_SHOP_DAILY,
    GET_CURRENT_SHOP_FEATURED,
    SET_DAILY_INFO
} from '../types';



const ShopReducer = (state, action) => {

    switch(action.type) {
        default:
            return state
        case SET_LOADING:
            return {
                ...state,
                loading: true
            }
        case GET_CURRENT_SHOP:
            return {
                ...state,
                CurrentShop: action.payload,
                loading: false
            }
        case GET_CURRENT_SHOP_DAILY:
            return {
                ...state,
                CurrentDaily: action.payload,
                loading: false
            }
        case GET_CURRENT_SHOP_FEATURED:
            return {
                ...state,
                CurrentFeatured: action.payload,
                loading: false
            }

    }
}

export default ShopReducer;
