import {useReducer} from 'react';
import ShopContext from './ShopContext';
import ShopReducer from './ShopReducer';
import {SET_LOADING, GET_CURRENT_SHOP} from '../types';
import axios from 'axios';


const ShopState = (props) => {
    

    const initialState = {
        loading: false,
        CurrentShop: {},
        CurrentDaily: [],
        CurrentFeatured: []
    }

    const [state, dispatch] = useReducer(ShopReducer, initialState);


    const SetLoading = () => {
        dispatch({type: SET_LOADING})
    }

    const GetCurrentShop = async () => {
        SetLoading();
        const response = await axios.get('https://fortnite-api.com/v2/shop/br');
        dispatch({
            type: GET_CURRENT_SHOP,
            payload: response.data.data
        })

        dispatch({
            type: GET_CURRENT_SHOP_DAILY,
            payload: response.data.data.daily
        })

        dispatch({
            type: GET_CURRENT_SHOP_FEATURED,
            payload: response.data.data.featured
        })
    }

    

    return <ShopContext.Provider value={{
        loading: state.loading,
        CurrentShop: state.CurrentShop,
        GetCurrentShop
    }}>

        {props.children}
        
    </ShopContext.Provider>;

}

export default ShopState;
