import React from 'react'
// import {Link} from 'react-router-dom'

const ShopItem = ({match, EntriesDetails}) => {

    const {items} = EntriesDetails;

    const ItemDescription = items.map((item, i) => {
        return item.description;
    })
    const ItemImages = items.map((item, i) => {
        return item.images;
    })

    const ImageSRC = ItemImages.map((image, pos) => {

        if (image.featured) {
            return image.featured
        } else {
            return image.icon;
        }
    })
    

    
    
    

    //images is an object, intro is an object,
    // rarity is an object, set is an object,
    // shophistory is a LIST,
    //type is an object,
    // variants can be null or prob an object
/*     const {description, 
        images,
        introduction,
        name,
        rarity,
        set,
        shopHistory,
        type,
        variants
    } = ItemDetails; */

    


    
   
    return (
        <div>
            <img src={ImageSRC} />
        </div>
    )
}

export default ShopItem
