import React, {useContext, useEffect} from 'react'
import SetContext from '../context/SetContext/SetContext';
import ItemSet from '../Item/ItemDetailsSection/Item Details Components/Item Set/ItemSet'
import Spinner from '../layout/spinner/Spinner';
import {Link} from 'react-router-dom'
import './Set.css'

const Set = ({match}) => {

    const setContext = useContext(SetContext);
    useEffect(() => {
       setContext.GetSet(match.params.setname)
        //eslint-disable-next-line
    }, [])

    const {SetName, SetInfo, loading, CardRarityStyles, ItemTypes} = setContext;
    {SetInfo && console.log(SetInfo)}


    const SetRarity = () => {
        if (SetInfo) {
                const CardType = SetInfo.map((item, i) => {
                    
                    const raritytype = item.rarity.displayValue;
                    
                    if(raritytype === 'Uncommon') {
                        return CardRarityStyles.uncommon;
                    } else if (raritytype === 'Epic') {
                        return CardRarityStyles.epic;
                    } else if (raritytype === 'Rare') {
                        return CardRarityStyles.rare;
                    } else if (raritytype === 'Icon Series') {
                        return CardRarityStyles.iconseries;
                    } else if (raritytype === 'Slurp Series') {
                        return CardRarityStyles.slurpseries;
                    } else if (raritytype === 'DARK SERIES') {
                        return CardRarityStyles.dark;
                    } else if (raritytype === 'Legendary') {
                        return CardRarityStyles.legendary;
                    }
                    
                })
                return CardType
            }
    }

    SetLinkByIDType: () => {

        if (SetInfo) {

                const returntype = SetInfo.map((item, i) => {

                    

                    const itemtype = item.id.split('_')[0];

                    
                    if (itemtype === 'CID') {
                        return ItemTypes.skin;
                    } else if (itemtype === 'EID') {
                        return ItemTypes.emote;
                    } else if (itemtype === 'MusicPack') {
                        return ItemTypes.musicpack;
                    } else if (itemtype === 'Pickaxe') {
                        return ItemTypes.pickaxe;
                    } else if (itemtype === 'Wrap') {
                        return ItemTypes.wrap;
                    } else if (itemtype === 'Glider') {
                        return ItemTypes.glider;
                    } else if (itemtype === 'BID') {
                        return ItemTypes.backbling
                    }
                })
                //eslint-disable-next-line
                return returntype;
        }
    }


    return (
        <div className='g'>

            <div className='set set--primary'>
                {loading ? <Spinner /> :  <>
                <div className="setsection setsection--primary">

                    <h2 className="set__head">
                        THE <span style={{color: '#ffe227'}}>{SetName} SET</span> ({SetInfo && SetInfo.length})
                    </h2>
                    <div className="setitemcontainer setitemcontainer--primary">
                        {SetInfo && SetInfo.map((item, i) => {
                            
                            return <Link>
                                        <div className='setitemcard setitemcard--primary' style={SetRarity()[i]}>
                                        <img src={item.images.icon} style={{height: '150px', width: '150px'}} />
                                    </div>
                                    </Link>
                            
                        })}
                    </div>
                </div>
                </>
                }
            </div>
        </div>
    )
}

export default Set
