    const sContext = useContext(ShopContext);

    return (
        <div className='featuredview featuredview--primary'>
            <p className='featuredview__head viewhead'>FEATURED</p>
            <div>
                {sContext.CurrentFeatured && sContext.CurrentFeatured.FeaturedItemItemsImages.map((it, i) => {

                    const commaregex = /,/gi

                    if(/,/gi.test(it)) {

                        const d = it.split(/,/gi);
                        console.log(d[0])
                        
                    }
                    return <img src={it} style={{height: '50px', width: '50px'}}/>
                    
                    
                })}
                {sContext.CurrentFeatured && sContext.CurrentFeatured.FeaturedItemfinalPrices.map((item, i) => {
                    return <p>{item}</p>
                })}
            </div>




        </div>
    )
}

export default FeaturedShopSection
