import {useReducer} from 'react';
import SetReducer from './SetReducer';
import SetContext from './SetContext';
import {SET_LOADING, GET_SET, SET_NAME} from '../types';
import axios from 'axios';

const SetState = (props) => {

    const InitialState = {
        SetInfo: null,
        SetName: null,
        loading: false
    }

    const [state, dispatch] = useReducer(SetReducer, InitialState);

    const SetLoading = () => {
        dispatch({type: SET_LOADING});
    }

    const GetSet = async (setname) => {


        const NameCharacterHandler = () => {
            
            const dashregex = /-/gi;
            
            // handles names with dashes (which are passed in with ~ instead)
            const tilderegex = /~/gi;


            if (dashregex.test(setname) === true && tilderegex.test(name) === false) {
                const NameDashResult = name.replaceAll(dashregex, ' ')
                return NameDashResult
            } else if (tilderegex.test(name) === true && dashregex.test(name) === false) {
                const NameTildeResult = name.replaceAll(tilderegex, '-')
                return NameTildeResult
            } else if (tilderegex.test(name) === true && dashregex.test(name) === true) {
                const replacedash = name.replaceAll(dashregex, ' ');
                const replacetilde = replacedash.replaceAll(tilderegex, '-');
                console.log(replacetilde)
                return replacetilde
            } else {
                return name
            }
        }


        const response = await axios.get(`https://fortnite-api.com/v2/cosmetics/br/search/all?set=${setname}`)


        
        dispatch({
            type: GET_SET,
            payload: response.data.data
        })

        dispatch({
            type: SET_NAME,
            payload: setname.toUpperCase()
        })
    }

    


    return <SetContext.Provider value={{

        SetInfo: state.SetInfo,
        SetName: state.SetName,
        loading: state.loading,
        GetSet
        
            }}>



                {props.children}


            </SetContext.Provider>
}


export default SetState
