import {useReducer} from 'react';
import ShopContext from './ShopContext';
import ShopReducer from './ShopReducer';
import {
    SET_LOADING,
    GET_CURRENT_SHOP,
    SET_DAILY,
    SET_FEATURED,
    SET_DAILY_PRICES,
    SET_FEATURED_PRICES
} from '../types';

import axios from 'axios';


const ShopState = (props) => {
    

    const initialState = {
        loading: false,
        CurrentFeatured: [],
        CurrentDaily: [],
    }

    const [state, dispatch] = useReducer(ShopReducer, initialState);


    const SetLoading = () => {
        dispatch({type: SET_LOADING})
    }

    // Get Current Shop
    const GetCurrentShop = async () => {
        SetLoading();
        const response = await axios.get('https://fortnite-api.com/v2/shop/br/combined');

        // FEATURED ITEM //
        const FeaturedItemfinalPrices = response.data.data.featured.entries.map((item, i) => {
            return item.finalPrice
        })
        const FeaturedItemBundleStatus = response.data.data.featured.entries.map((item, i) => {
            return item.bundle
        })

        const FeaturedItemItemsArr = response.data.data.featured.entries.map((item, i) => {
            return item.items
        })

        const FeaturedItemObj = {
            FeaturedItemfinalPrices,
            FeaturedItemBundleStatus,
            FeaturedItemItemsArr
        }

        console.log(FeaturedItemObj)

        dispatch({
            type: GET_CURRENT_SHOP
        }) // changes loading to confirm shop info acquired
        dispatch({
            type: SET_FEATURED,
            payload: response.data.data.featured.entries
        })
        dispatch({
            type: SET_DAILY,
            payload: response.data.data.daily.entries
        })
    }

    
    

    return <ShopContext.Provider value={{
        loading: state.loading,
        CurrentDaily: state.CurrentDaily, 
        CurrentFeatured: state.CurrentFeatured, 
        GetCurrentShop
    }}>

        {props.children}

    </ShopContext.Provider>;

}

export default ShopState;
