import React from 'react';
import ShopContext from '../../layout'
// import {Link} from 'react-router-dom'

const ShopItem = ({match, EntriesDetails}) => {

    const {items} = EntriesDetails;

    const ItemDescription = items.map((item, i) => {
        return item.description;
    })

    const ItemImages = items.map((item, i) => {
        return item.images;
    })

    const ImageSRC = ItemImages.map((image, pos) => {

        if (image.featured) {
            return image.featured;
        } else {
            return image.icon;
        }
    })
   
    return (
        <div>
            <p style={{fontFamily: 'Fortnite', fontSize: '1.5em'}}>{ItemDescription}</p>
            <img src={ImageSRC} style={{height: '100px', width: '100px'}}/>
        </div>
    )
}

export default ShopItem
