                            <img src={VBUCKS} className='itemcardprice__image' />
                            <p className='itemcardprice__text'>{iContext.itemprice && iContext.itemprice}</p>
                        </div>
                    </div>
            </div>
        </div>
    )
}

export default ItemCard
