import {SET_LOADING, GET_CURRENT_SHOP} from '../types';



const ShopReducer = (state, action) => {

    switch(action.type) {
        default:
            return state
        case SET_LOADING:
            return {
                ...state,
                loading: true
            }
        case GET_CURRENT_SHOP:
            return {
                ...state,
                loading: false,
                CurrentShop
            }
    }
}

export default ShopReducer;
