import React, {useContext, useEffect} from 'react';
import ShopContext from '../context/ShopContext/ShopContext';
import './Shop.css';
import Spinner from '../../media/images/LoadingSpinner.svg'

const Shop = (props) => {
    
    const sContext = useContext(ShopContext);

    useEffect(() => {
        sContext.GetCurrentShop()
    }, [])
    
    if(sContext.loading && <Spinner/>)
    return (
        <div>
            <h1>
                Fortnite Item Shop
            </h1>
            {}
        </div>
    )
}

export default Shop
