import React, {useContext, useEffect} from 'react';
import ShopContext from '../context/ShopContext/ShopContext';
//import Spinner from '../../media/images/Spinner.svg'
import FeaturedShopItem from './ShopItem/FeaturedShopItem'
import DailyShopItem from './ShopItem/DailyShopItem'
import './Shop.css';

const Shop = (props) => {
    
    const sContext = useContext(ShopContext);

    useEffect(() => {
        sContext.GetCurrentShop()
        // eslint-disable-next-line
    }, [])
    
    return (
        <div className='shop shop--primary'>
            <h1 className='shopview__head'>Today's Shop</h1>
            <div className="shopview shopview--primary">
                <div className='featuredview featuredview--primary'>
                    <p className='featuredview__head viewhead'>FEATURED</p>
                    <div>
                        {sContext.CurrentFeatured.length > 0 && sContext.CurrentFeatured.map((item, i) => {
                            return <ShopItem FeaturedItems={item} />
                        })}
                    </div>
                </div>
                <div className='dailyview dailyview--primary'>
                    <p className='dailyview__head viewhead'>DAILY</p>
                    <div>
                        {sContext.CurrentDaily.length > 0 && sContext.CurrentDaily.map((item, i) => {
                            return <DailyShopItem DailyItems={item} />
                        })}
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Shop
